table.sql:

create table sys_dz_category
(
category_id          varbinary(32) not null comment '类别内码',
category_name        varchar(64) not null comment '类别名称',
is_valid             int(1) comment '是否有效',
sort_num             int(11) comment '排序号',
input_date           datetime comment '录入时间',
primary key (category_id)
);

alter table sys_dz_category comment '字典类别';
